sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();
//rightNow.addSeconds(36000); //convert to local time
//gs.info(rightNow.toString());

var oneDayFromNow = new GlideDateTime();
oneDayFromNow.addDaysLocalTime(1);


// Query the database for NeedIt Task records with Due date field values older
// than the current time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var dueSoonNITasks = new GlideRecord('x_58872_needit_needit_task');
dueSoonNITasks.addQuery('due_date','>=',rightNow);
dueSoonNITasks.addQuery('due_date','<=',oneDayFromNow);
dueSoonNITasks.addQuery('state','!=',3);

dueSoonNITasks.query();
// Write a log message for each overdue NeedIt Task Record
while(dueSoonNITasks.next()){
	gs.info("Due Soon NeedIt Task record = " + dueSoonNITasks.number);
}
